DROP TABLE IF EXISTS `goods_local`;
CREATE TABLE `goods_local` (
  `id` int NOT NULL AUTO_INCREMENT,
  `local_name` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `name_id` int DEFAULT NULL,
  `currency` varchar(16) COLLATE utf8mb4_general_ci DEFAULT 'US',
  PRIMARY KEY (`id`),
  KEY `name_id` (`name_id`),
  CONSTRAINT `goods_local_ibfk_1` FOREIGN KEY (`name_id`) REFERENCES `goods` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `image`;
